select d.index_name as name, a.shangpin_order_types as groupColumn, sum(a.shangpin_order_true_price) as value from shangpin_order a LEFT JOIN dictionary d ON a.shangpin_order_types=d.code_index d.dic_code= 'shangpin_order_types'
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY a.shangpin_order_types

select d.index_name as name, a.shangpin_order_types as groupColumn, count(*) as value from shangpin_order a LEFT JOIN dictionary d ON a.shangpin_order_types=d.code_index where d.dic_code= 'shangpin_order_types'
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY a.shangpin_order_types

select d.index_name as name, a.shangpin_order_types as groupColumn, sum(a.shangpin_order_true_price) as value from shangpin_order a LEFT JOIN dictionary d ON a.shangpin_order_types=d.code_index where d.dic_code= 'shangpin_order_types' -- 字典表 -->
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY a.shangpin_order_types

select d.index_name as name, a.shangpin_order_types as groupColumn, count(*) as value from shangpin_order a LEFT JOIN dictionary d ON a.shangpin_order_types=d.code_index where d.dic_code= 'shangpin_order_types'
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY a.shangpin_order_types

select date_format(a.insert_time, '%Y-%m') as name1, s.shangpin_name as name2, sum(a.shangpin_order_true_price) as value from shangpin_order a left JOIN shangpin s on a.shangpin_id=s.id where 1=1
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY date_format(a.insert_time, '%Y-%m'),a.shangpin_id ORDER BY name1 asc

select date_format(a.insert_time, '%Y-%m') as name1, s.shangpin_name as name2, count(*) as value from shangpin_order a left JOIN shangpin s on a.shangpin_id=s.id where 1=1
AND date_format(a.insert_time, "%Y-%m") = #{riqi}
GROUP BY date_format(a.insert_time, '%Y-%m'),a.shangpin_id ORDER BY name1 asc

select
	d.index_name as name,
	a.${groupColumn} as groupColumn, 
	sum(a.${sumColumn}) as value
from ${tableName} a
	LEFT JOIN dictionary d ON a.${groupColumn}=d.code_index where d.dic_code= #{groupColumn}

	AND date_format(a.insert_time, "%Y-%m") = #{riqi} 

GROUP BY a.${groupColumn}

select
	d.index_name as name,
	a.${groupColumn} as groupColumn, 
	count(*) as value
from ${tableName} a
LEFT JOIN dictionary d ON a.${groupColumn}=d.code_index where d.dic_code= #{groupColumn}

	AND date_format(a.insert_time, "%Y-%m") = #{riqi} 

GROUP BY a.${groupColumn}

select


		date_format(thisTableName.${item}, '%Y-%m') as thisDate${index}




		date_format(joinTableName.${item}, '%Y-%m') as joinDate${index}




		thisTableName.${item} as thisString${index}




		joinTableName.${item} as joinString${index}




		${item}.index_name as thisTypes${index}




		${item}.index_name as joinTypes${index}


sum(thisTableName.${thisTable.sumColum}) as value
from ${thisTable.tableName} thisTableName

	LEFT JOIN ${joinTable.tableName} joinTableName on thisTableName.${joinTable.tableName}_id = joinTableName.id






				LEFT JOIN dictionary ${item} ON thisTableName.${item}=${item}.code_index
					WHERE ${item}.dic_code= #{item}

					AND date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}





				LEFT JOIN dictionary ${item} ON joinTableName.${item}=${item}.code_index
					WHERE ${item}.dic_code= #{item}

					AND date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}






			WHERE date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}



GROUP BY


		date_format(thisTableName.${item}, '%Y-%m')



			,







		date_format(joinTableName.${item}, '%Y-%m')



			,







		thisTableName.${item}



			,







		joinTableName.${item}



			,







		thisTableName.${item}



			,







		joinTableName.${item}


ORDER BY value DESC

select


		date_format(thisTableName.${item}, '%Y-%m') as thisDate${index}




		date_format(joinTableName.${item}, '%Y-%m') as joinDate${index}




		thisTableName.${item} as thisString${index}




		joinTableName.${item} as joinString${index}




		${item}.index_name as thisTypes${index}




		${item}.index_name as joinTypes${index}


count(*) as value
from ${thisTable.tableName} thisTableName

	LEFT JOIN ${joinTable.tableName} joinTableName on thisTableName.${joinTable.tableName}_id = joinTableName.id






				LEFT JOIN dictionary ${item} ON thisTableName.${item}=${item}.code_index
				WHERE ${item}.dic_code= #{item}

					AND date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}





				LEFT JOIN dictionary ${item} ON joinTableName.${item}=${item}.code_index
				WHERE ${item}.dic_code= #{item}

					AND date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}






			WHERE date_format(thisTableName.insert_time, #{dateFormat}) = #{riqi}



GROUP BY


		date_format(thisTableName.${item}, '%Y-%m')



			,







		date_format(joinTableName.${item}, '%Y-%m')



			,







		thisTableName.${item}



			,







		joinTableName.${item}



			,







		thisTableName.${item}



			,







		joinTableName.${item}


ORDER BY value DESC
